create table Student
(
    Id             int identity
        constraint PK_Student
            primary key,
    StudentCode    nvarchar(max) default N'bJ34Ty',
    FullName       nvarchar(255) collate SQL_Latin1_General_CP1_CI_AI,
    Gender         nvarchar(20)                                         not null,
    Email          nvarchar(50)                                         not null,
    PhoneNumber    nvarchar(20)                                         not null,
    DateOfBirth    datetime2,
    EnrollmentDate datetime2     default '2024-10-09T08:13:31.4216050Z' not null,
    Status         nvarchar(50)  default N'Chưa có lớp'
)
go

